    private void btnTinhActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTinhActionPerformed
        // TODO add your handling code here:
        String soa = txtSOA.getText();
        String sob = txtSOB.getText();
        String soc = txtSOC.getText();
        double a = Double.parseDouble(soa);
        double b = Double.parseDouble(sob);
        double c = Double.parseDouble(soc);
        double denta = b * b - 4 * c * a;
       
        if (a == 0) {
            double x;
            x = -b / c;
            txtketqua.setText("" + x);
           
        } else {
            if (denta > 0) {
                //pt có 2 nghiệm pb.
                double x1, x2;
                x1 = (-b + Math.sqrt(denta)) / (2 * a);
                x2 = (-b - Math.sqrt(denta)) / (2 * a);
                txtKETQUA1.setText("" + x1);
                txtKETQUA2.setText("" + x2);
            } else {
                if (denta == 0) {
                    //pt có 1 nghiệm duy nhất
                    double x;
                    x = -b / (2 * a);
                    txtketqua.setText("" + x);
                } else {
                    txtketqua.setText("phương trình vô nghiệm.");
                }
            }
        }
        
    }//GEN-LAST:event_btnTinhActionPerformed

